// App.js
import React, { useState } from 'react';
import 'C:/Users/Asus/Desktop/to-do/todo-app/src/index.css';
import { FaEdit, FaTrash } from 'react-icons/fa'; // Importing icons from Font Awesome

function App(props) {
  const [tasks, setTasks] = useState([
    { id: 0, text: "Eat", completed: true },
    { id: 1, text: "Sleep", completed: false },
    { id: 2, text: "Repeat", completed: false }
  ]);
  const [newTask, setNewTask] = useState("");
  const [filter, setFilter] = useState("all");
  const [editTaskId, setEditTaskId] = useState(null);
  const [editTaskText, setEditTaskText] = useState("");

  const handleChange = (event) => {
    setNewTask(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (newTask.trim() === "") return;
    const newTaskObj = {
      id: tasks.length,
      text: newTask,
      completed: false
    };
    setTasks([...tasks, newTaskObj]);
    setNewTask("");
  };

  const toggleTaskCompletion = (taskId) => {
    const updatedTasks = tasks.map(task =>
      task.id === taskId ? { ...task, completed: !task.completed } : task
    );
    setTasks(updatedTasks);
  };

  const deleteTask = (taskId) => {
    const updatedTasks = tasks.filter(task => task.id !== taskId);
    setTasks(updatedTasks);
  };

  const handleFilterChange = (filter) => {
    setFilter(filter);
  };

  const handleEdit = (taskId, taskText) => {
    setEditTaskId(taskId);
    setEditTaskText(taskText);
  };

  const handleEditChange = (event) => {
    setEditTaskText(event.target.value);
  };

  const handleEditSubmit = (taskId) => {
    const updatedTasks = tasks.map(task =>
      task.id === taskId ? { ...task, text: editTaskText } : task
    );
    setTasks(updatedTasks);
    setEditTaskId(null);
    setEditTaskText("");
  };

  const handleKeyPress = (event, taskId) => {
    if (event.key === 'Enter') {
      handleEditSubmit(taskId);
    }
  };

  let filteredTasks = tasks;
  if (filter === "active") {
    filteredTasks = tasks.filter(task => !task.completed);
  } else if (filter === "completed") {
    filteredTasks = tasks.filter(task => task.completed);
  }

  return (
    <div className="todoapp stack-large">
      <h1>TodoMatic</h1>
      <form onSubmit={handleSubmit}>
        <h2 className="label-wrapper">
          <label htmlFor="new-todo-input" className="label__lg">
            What needs to be done?
          </label>
        </h2>
        <input
          type="text"
          id="new-todo-input"
          className="input input__lg"
          name="text"
          autoComplete="off"
          value={newTask}
          onChange={handleChange}
        />
        <button type="submit" className="btn btn__primary btn__lg">
          Add
        </button>
      </form>
      <div className="filters btn-group stack-exception">
        <button
          type="button"
          className={`btn toggle-btn ${filter === "all" ? "active" : ""}`}
          onClick={() => handleFilterChange("all")}
        >
          <span className="visually-hidden">Show </span>
          <span>All</span>
          <span className="visually-hidden"> tasks</span>
        </button>
        <button
          type="button"
          className={`btn toggle-btn ${filter === "active" ? "active" : ""}`}
          onClick={() => handleFilterChange("active")}
        >
          <span className="visually-hidden">Show </span>
          <span>Active</span>
          <span className="visually-hidden"> tasks</span>
        </button>
        <button
          type="button"
          className={`btn toggle-btn ${filter === "completed" ? "active" : ""}`}
          onClick={() => handleFilterChange("completed")}
        >
          <span className="visually-hidden">Show </span>
          <span>Completed</span>
          <span className="visually-hidden"> tasks</span>
        </button>
      </div>
      <h2 id="list-heading">{filteredTasks.length} tasks remaining</h2>
      <ul
        role="list"
        className="todo-list stack-large stack-exception"
        aria-labelledby="list-heading"
      >
        {filteredTasks.map(task => (
          <li key={task.id} className="todo stack-small">
            <div className="c-cb">
              <input
                id={`todo-${task.id}`}
                type="checkbox"
                checked={task.completed}
                onChange={() => toggleTaskCompletion(task.id)}
              />
              <label
                className={`todo-label ${editTaskId === task.id ? "hidden" : ""}`}
                htmlFor={`todo-${task.id}`}
                onDoubleClick={() => handleEdit(task.id, task.text)}
              >
                {task.text}
              </label>
              {editTaskId === task.id && (
                <div className="edit-task">
                  <input
                    type="text"
                    className="input input__lg"
                    value={editTaskText}
                    onChange={handleEditChange}
                    autoFocus
                    onKeyPress={(e) => handleKeyPress(e, task.id)}
                  />
                  <div className="task-actions">
                    <button type="button" onClick={() => handleEditSubmit(task.id)}>
                      Save
                    </button>
                  </div>
                </div>
              )}
            </div>
            <div className="task-actions">
              <button type="button" onClick={() => handleEdit(task.id, task.text)}>
                <FaEdit />
                <span className="visually-hidden">Edit {task.text}</span>
              </button>
              <button type="button" onClick={() => deleteTask(task.id)}>
                <FaTrash />
                <span className="visually-hidden">Delete {task.text}</span>
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
