// App.js
import React, { useState } from 'react';
import 'C:/Users/Asus/Desktop/to-do/todo-app/src/index.css';


function App(props) {
  const [tasks, setTasks] = useState([
    { id: 0, text: "Eat", completed: true, dueDate: new Date() },
    { id: 1, text: "Sleep", completed: false, dueDate: new Date() },
    { id: 2, text: "Repeat", completed: false, dueDate: new Date() }
  ]);
  const [newTask, setNewTask] = useState("");
  const [filter, setFilter] = useState("all");
  const [editTaskId, setEditTaskId] = useState(null);
  const [editTaskText, setEditTaskText] = useState("");

  const handleChange = (event) => {
    setNewTask(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (newTask.trim() === "") return;
    const newTaskObj = {
      id: tasks.length,
      text: newTask,
      completed: false,
      dueDate: new Date()
    };
    setTasks([...tasks, newTaskObj]);
    setNewTask("");
  };

  const toggleTaskCompletion = (taskId) => {
    const updatedTasks = tasks.map(task =>
      task.id === taskId ? { ...task, completed: !task.completed } : task
    );
    setTasks(updatedTasks);
  };

  const deleteTask = (taskId) => {
    const updatedTasks = tasks.filter(task => task.id !== taskId);
    setTasks(updatedTasks);
  };

  const handleFilterChange = (filter) => {
    setFilter(filter);
  };

  const handleEdit = (taskId, taskText) => {
    setEditTaskId(taskId);
    setEditTaskText(taskText);
  };

  const handleEditChange = (event) => {
    setEditTaskText(event.target.value);
  };

  const handleEditSubmit = (taskId) => {
    const updatedTasks = tasks.map(task =>
      task.id === taskId ? { ...task, text: editTaskText } : task
    );
    setTasks(updatedTasks);
    setEditTaskId(null);
    setEditTaskText("");
  };

  const today = new Date();
  const tomorrow = new Date(today);
  tomorrow.setDate(tomorrow.getDate() + 1);

  let filteredTasks = tasks;
  if (filter === "active") {
    filteredTasks = tasks.filter(task => !task.completed);
  } else if (filter === "completed") {
    filteredTasks = tasks.filter(task => task.completed);
  } else if (filter === "today") {
    filteredTasks = tasks.filter(task => task.dueDate.toDateString() === today.toDateString());
  } else if (filter === "tomorrow") {
    filteredTasks = tasks.filter(task => task.dueDate.toDateString() === tomorrow.toDateString());
  } else if (filter === "future") {
    filteredTasks = tasks.filter(task => task.dueDate > tomorrow);
  }

  return (
    <div className="todoapp stack-large">
      <h1>TodoMatic</h1>
      <form onSubmit={handleSubmit}>
        <h2 className="label-wrapper">
          <label htmlFor="new-todo-input" className="label__lg">
            What needs to be done?
          </label>
        </h2>
        <input
          type="text"
          id="new-todo-input"
          className="input input__lg"
          name="text"
          autoComplete="off"
          value={newTask}
          onChange={handleChange}
        />
        <button type="submit" className="btn btn__primary btn__lg">
          Add
        </button>
      </form>
      <div className="filters btn-group stack-exception">
        <button
          type="button"
          className={`btn toggle-btn ${filter === "all" ? "active" : ""}`}
          onClick={() => handleFilterChange("all")}
        >
          All
        </button>
        <button
          type="button"
          className={`btn toggle-btn ${filter === "active" ? "active" : ""}`}
          onClick={() => handleFilterChange("active")}
        >
          Active
        </button>
        <button
          type="button"
          className={`btn toggle-btn ${filter === "completed" ? "active" : ""}`}
          onClick={() => handleFilterChange("completed")}
        >
          Completed
        </button>
        <button
          type="button"
          className={`btn toggle-btn ${filter === "today" ? "active" : ""}`}
          onClick={() => handleFilterChange("today")}
        >
          Today
        </button>
        <button
          type="button"
          className={`btn toggle-btn ${filter === "tomorrow" ? "active" : ""}`}
          onClick={() => handleFilterChange("tomorrow")}
        >
          Tomorrow
        </button>
        <button
          type="button"
          className={`btn toggle-btn ${filter === "future" ? "active" : ""}`}
          onClick={() => handleFilterChange("future")}
        >
          Future
        </button>
      </div>
      <h2 id="list-heading">{filteredTasks.length} tasks remaining</h2>
      <ul
        role="list"
        className="todo-list stack-large stack-exception"
        aria-labelledby="list-heading"
      >
        {filteredTasks.map(task => (
          <li key={task.id} className="todo stack-small">
            <div className="c-cb">
              <input
                id={`todo-${task.id}`}
                type="checkbox"
                checked={task.completed}
                onChange={() => toggleTaskCompletion(task.id)}
              />
              <label
                className={`todo-label ${editTaskId === task.id ? "hidden" : ""}`}
                htmlFor={`todo-${task.id}`}
                onDoubleClick={() => handleEdit(task.id, task.text)}
              >
                {task.text}
              </label>
              {editTaskId === task.id && (
                <input
                  type="text"
                  className="input input__lg"
                  value={editTaskText}
                  onChange={handleEditChange}
                  onBlur={() => handleEditSubmit(task.id)}
                  autoFocus
                />
              )}
            </div>
            <div className="btn-group">
              <button type="button" className="btn" onClick={() => handleEdit(task.id, task.text)}>
                Edit
              </button>
              <button
                type="button"
                className="btn btn__danger"
                onClick={() => deleteTask(task.id)}
              >
                Delete
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
